REAL_TYPE
sqrt_approx(REAL_TYPE n) {
    n = 1.0f / n;
    long i;
    REAL_TYPE x, y;

    x = n * 0.5f;
    y = n;
    i = *(long *)&y;
    i = 0x5f3759df - (i >> 1);
    y = *(REAL_TYPE *)&i;
    y = y * (1.5f - (x * y * y));

    return y;
}

#define SQRT_MAGIC_F 0x5f3759df 
REAL_TYPE  sqrt2(const REAL_TYPE x)
{
  union // get bits for floating value
  {
    REAL_TYPE x;
    int i;
  } u;
  u.x = x;
  u.i = SQRT_MAGIC_F - (u.i >> 1);  // gives initial guess y0

  const REAL_TYPE xux = x*u.x;

  return xux*(1.5f - .5f*xux*u.x);// Newton step, repeating increases accuracy 
}


REAL_TYPE 
cos_approx(REAL_TYPE k)
{
	int i, n=4;
    float x, sum=1, t=1;
	x = k*3.14159/180;
	/* Loop to calculate the value of Cosine */
	#pragma loopbound min 4 max 4
	for(i=1;i<=n;i++)
	{
		t = t * (-1) * x * x/(2*i*(2*i-1));
		sum = sum + t;
	}
	return sum;
}

REAL_TYPE 
sin_approx(REAL_TYPE k)
{
	int i, n=4;
    float x, sum=1, t=1;
	x=x*3.14159/180;
    t=x;
    sum=x; 
    /* Loop to calculate the value of Sine */
	#pragma loopbound min 4 max 4
    for(i=1;i<=n;i++)
    {
        t=(t*(-1)*x*x)/(2*i*(2*i+1));
        sum=sum+t;
    }
	return sum;
}

/* Va filter 100 Hz */
REAL_TYPE
Va_filter_100(REAL_TYPE Va) {
	static REAL_TYPE y  = 0.0;
	static REAL_TYPE x1 = 0.0;
	static REAL_TYPE x2 = 0.0;
	static REAL_TYPE x1_tmp = 0.0;
	static REAL_TYPE x2_tmp = 0.0;
	static unsigned short debut = 1;
	/* 100 Hz coefficients */
	static REAL_TYPE a0 = 0.956543675476034;
	static REAL_TYPE a1 = -1.955578398054313;
	static REAL_TYPE b0 = 0.000479064865372430;
	static REAL_TYPE b1 =  0.000486212556348925;

	if (debut) {
		debut  = 0;
		x1     = Va_eq * (1.0 + a1 - b1);
		x2     = Va_eq;
	}
	// Output
	y = x2;
	// State
	x1_tmp =     - a0 * x2 + b0 * Va;
	x2_tmp =  x1 - a1 * x2 + b1 * Va;
	// Update
	x1 = x1_tmp;
	x2 = x2_tmp;

	return y;
} /* end of Va filter 100 Hz */

